const handler = async (event) => {
  try {
    const body = event.body;
    const htmlContent = body.html || body?.body?.html;

    if (!htmlContent) {
      return {
        statusCode: 400,
        body: JSON.stringify({
          error: "No HTML content provided",
        }),
      };
    }

    extractContent(htmlContent, "");

    // Get start and end time
    const timeStart = extractContent(
      htmlContent,
      'event-time-12hr-start"',
      "</time>"
    ).replace("\u202f", "");
    const timeEnd = extractContent(
      htmlContent,
      'event-time-12hr-end"',
      "</time>"
    ).replace("\u202f", "");

    // Extract all content sections
    const description = getAllContent(htmlContent)
      .filter((text) => text.trim()) // Remove empty strings
      .join("\n");

    const eventData = {
      date: extractContent(
        htmlContent,
        'time class="event-date" datetime="',
        '"'
      ),
      startTime: convertTo24Hour(timeStart),
      endTime: convertTo24Hour(timeEnd),
      title: extractContent(htmlContent, 'class="eventitem-title">', "</h1>"),
      description: cleanText(description),
    };

    if (!eventData.date || !eventData.title) {
      return {
        statusCode: 422,
        body: JSON.stringify({
          error: "Could not parse event data from HTML",
        }),
      };
    }

    return {
      statusCode: 200,
      headers: {
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify(eventData),
    };
  } catch (error) {
    console.error("Error processing request:", error);
    return {
      statusCode: 500,
      body: JSON.stringify({
        error: "Internal server error",
        message: error.message,
      }),
    };
  }
};

function extractContent(html, startMarker, endMarker) {
  const startIndex = html.indexOf(startMarker);
  if (startIndex === -1) return "";

  const contentStart = startIndex + startMarker.length;
  const contentEnd = html.indexOf(endMarker, contentStart);
  if (contentEnd === -1) return "";

  return html.substring(contentStart, contentEnd);
}

function getAllContent(html) {
  // Find all content within sqs-html-content divs
  const contents = [];
  let startIndex = 0;

  while (true) {
    const divStart = html.indexOf('class="sqs-html-content">', startIndex);
    if (divStart === -1) break;

    const contentStart = divStart + 'class="sqs-html-content">'.length;
    const contentEnd = html.indexOf("</div>", contentStart);
    if (contentEnd === -1) break;

    let content = html.substring(contentStart, contentEnd);

    // Extract text from paragraphs
    content = content.replace(/<p[^>]*>(.*?)<\/p>/g, "$1");
    // Remove remaining HTML tags
    content = content.replace(/<[^>]*>/g, "");
    // Clean up whitespace
    content = content.replace(/\s+/g, " ").trim();

    if (content) {
      contents.push(content);
    }

    startIndex = contentEnd;
  }

  return contents;
}

function cleanText(text) {
  return text
    .replace(/&quot;/g, '"')
    .replace(/&amp;/g, "&")
    .replace(/&lt;/g, "<")
    .replace(/&gt;/g, ">")
    .replace(/\s+/g, " ")
    .trim();
}

function convertTo24Hour(time) {
  time = time.split(">")[1];
  const [hour, minutePart] = time.split(":");
  const [minute, period] = minutePart.split(" ");
  let hour24 = parseInt(hour);

  if (period === "PM" && hour24 !== 12) {
    hour24 += 12;
  }
  if (period === "AM" && hour24 === 12) {
    hour24 = 0;
  }

  return `${hour24.toString().padStart(2, "0")}:${minute}`;
}

